/** Change the settings to your likings */
const settings = {
	panels: {
		/** The number of rows of the grid */
		rows: 5,
		/** The number of columns of the grid */
		cols: 5
	},
	chance: {
		/** How many wins the grid should have */
		wins: 25,
		/** How many tries the user have to find a win-panel */
		attempts: 25
	},
	/**
		Use the captions as link-tags. There you can add the coupon-parameters which can be read from another page.
		This can be a simple coupon-code inside a contact-form or a dynamic generated coupon from shop-systems as WooCommerce, Shopify, Magento, etc.
	*/
	captions: {
		/** Neccessary: The caption of a failure-panel */
		failed: { caption: `<span style='color: #da0000'>未中</span>` },
		/** Neccessary: The caption of a won-panel (default) */
		default: { caption: `<span style='color: #18d100'>1</span>` },
		/** Optional: Overwrites a special-type of price */
		1: { caption: `<span style='color: #18d100'>2</span>` },
		2: { caption: `<span style='color: #18d100'>3</span>` },
		3: { caption: `<span style='color: #18d100'>4</span>` },
		4: { caption: `<span style='color: #18d100'>5</span>` },
		5: { caption: `<span style='color: #18d100'>6</span>` },
		6: { caption: `<span style='color: #18d100'>7</span>` },
		7: { caption: `<span style='color: #18d100;'>8</span>` },
		8: { caption: `<span style='color: #18d100;'>9</span>` },
		9: { caption: `<span style='color: #18d100;'>10</span>` },
		10: { caption: `<span style='color: #18d100;'>11</span>` },
		11: { caption: `<span style='color: #18d100;'>9</span>` },
		12: { caption: `<span style='color: #18d100;'>9</span>` },
		13: { caption: `<span style='color: #18d100;'>9</span>` }
	}
};
/** The number of the panels in grid (rows x columns) */
const panels_count = settings.panels.rows * settings.panels.cols;


function generate_random_panels( max_panels, win_chances ) {

	/** Shuffled Array of panels (0 = Fail | >0 = Win) */
	var panels = [
		...Array.from( Array( win_chances ).keys() )
	].sort( ( a, b ) => 0.5 - Math.random() );
	return panels;

}

function create_panel_html( max_panels ) {

	const panel_array = generate_random_panels( max_panels, settings.chance.wins );

    console.log(panel_array);
	let html = '';
	/* You can add your own values to this panels, but keep the structure! */
	for( var i=0; i < max_panels; i++ ) {
	
		var panel_type = panel_array[ i ];
		var caption = settings.captions.failed.caption;
        if(panel_type<=0)panel_type=max_panels.length;
		if( panel_type >= 1 ) {
			panel_type = panel_type.toString();
			caption = `<span style='color: #18d100;'>${panel_type}</span>`
		}

		html += `
			<div class="panel_wrapper" style="width:160px">
               
                 <p>${i+1}</p>
				<div class="panel" id="container-${ i }" style="max-width: 150px">
					<canvas class="canvas" id="canvas-${ i }" width="150px" height="150px" data-choosen="false"></canvas>
					<div class="price">${caption}</div>
				</div>
			</div>
		`;

	}

	return html;

}

const canvas_wrapper = document.getElementById( 'canvas_wrapper' );
canvas_wrapper.innerHTML = create_panel_html( panels_count );




/*
You can use a base64-encoder to add your own image like following: https://www.base64-image.de/
Keep in mind that the image has to be 150x150 pixels.
*/
const image_b64 = 'data:image/png;base64,';
const brush_b64 = 'data:image/png;base64,';

for( var i=0; i < panels_count; i++ ) {

	(function( i ) {

		'use strict';

		var container_id = 'container-'+i;
		var canvas_id = 'canvas-'+i;

		var isDrawing, lastPoint;
		var container    = document.getElementById( container_id ),
			canvas       = document.getElementById( canvas_id ),
			canvasWidth  = canvas.width,
			canvasHeight = canvas.width,
			ctx          = canvas.getContext( '2d' ),
			image        = new Image(),
			brush        = new Image();

		image.src = image_b64;
		image.onload = function() {

			ctx.drawImage( image, 0, 0 );

		};
		brush.src = brush_b64;

		canvas.addEventListener( 'mousedown', handleMouseDown, false );
		canvas.addEventListener( 'touchstart', handleMouseDown, false );
		canvas.addEventListener( 'mousemove', handleMouseMove, false );
		canvas.addEventListener( 'touchmove', handleMouseMove, false );
		canvas.addEventListener( 'mouseup', handleMouseUp, false );
		canvas.addEventListener( 'touchend', handleMouseUp, false );

		function distanceBetween( point1, point2 ) {

			return Math.sqrt(Math.pow( point2.x - point1.x, 2 ) + Math.pow( point2.y - point1.y, 2 ) );

		}

		function angleBetween( point1, point2 ) {

			return Math.atan2( point2.x - point1.x, point2.y - point1.y );

		}

		// Only test every `stride` pixel. `stride`x faster,
		// but might lead to inaccuracy
		function getFilledInPixels(stride) {
			if( !stride || stride < 1 ) {
				stride = 1;
			}

			var pixels   = ctx.getImageData(0, 0, canvasWidth, canvasHeight),
				pdata    = pixels.data,
				l        = pdata.length,
				total    = ( l / stride ),
				count    = 0;

			// Iterate over all pixels
			for( var i = count = 0; i < l; i += stride ) {
				if( parseInt( pdata[ i ] ) === 0 ) {
					count++;
				}
			}

			return Math.round( ( count / total ) * 100 );
		}

		function getMouse( e, canvas ) {

			var offsetX = 0,
				offsetY = 0,
				mx, my;

			if( canvas.offsetParent !== undefined ) {
				do {
					offsetX += canvas.offsetLeft;
					offsetY += canvas.offsetTop;
				} while( ( canvas = canvas.offsetParent ) );
			}

			mx = ( e.pageX || e.touches[0].clientX ) - offsetX;
			my = ( e.pageY || e.touches[0].clientY ) - offsetY;

			return { x: mx, y: my };

		}

		function handlePercentage( filledInPixels ) {

			filledInPixels = filledInPixels || 0;

			// The user must scratch at least 80% of the card to unlock it. 
			// You can change this value if you want.
			if( filledInPixels > 80 ) {

				canvas.parentNode.removeChild( canvas );

			}

		}

		function handleMouseDown( e ) {

			if( settings.chance.attempts > 0 || this.getAttribute( 'data-choosen' ) == "true" ) {
				
				if( this.getAttribute( 'data-choosen' ) == "false" ) {
					
					settings.chance.attempts--;
					
					
				}
				
				this.setAttribute( 'data-choosen', 'true' );
				
				isDrawing = true;
				lastPoint = getMouse( e, canvas );

			}

		}

		function handleMouseMove(e) {

			if( !isDrawing ) {

				return;

			}

			e.preventDefault();

			var currentPoint = getMouse(e, canvas),
				dist = distanceBetween(lastPoint, currentPoint),
				angle = angleBetween(lastPoint, currentPoint),
				x, y;

			for (var i = 0; i < dist; i++) {

				x = lastPoint.x + ( Math.sin( angle ) * i ) - 25;
				y = lastPoint.y + ( Math.cos( angle ) * i ) - 25;
				ctx.globalCompositeOperation = 'destination-out';
				ctx.drawImage( brush, x, y );

			}

			lastPoint = currentPoint;
			handlePercentage( getFilledInPixels( 1 ) );

		}

		function handleMouseUp( e ) {

			isDrawing = false;

		}

	})( i );

}